import React, { useState, useEffect } from 'react';
import { Box, Container, Grid, Link, Typography, SvgIcon } from '@mui/material';
import Search from './../Search/Search';
import { fetchWeatherData } from '../../api/Api';
import { transformDateFormat } from './../../utilities/DatetimeUtils';
import UTCDatetime from './../Reusable/UTCDatetime';
import LoadingBox from './../Reusable/LoadingBox';
import { ReactComponent as SplashIcon } from './../../assets/splash-icon.svg';
import Logo from './../../assets/logo.png';
import ErrorBox from './../Reusable/ErrorBox';
import { ALL_DESCRIPTIONS } from './../../utilities/DateConstants';
import HomeIcon from '@mui/icons-material/Home';
import { getTodayForecastWeather, getWeekForecastWeather } from './../../utilities/DataUtils';
import { useLocation } from 'react-router-dom';
import TodayWeather from '../TodayWeather/TodayWeather';
import WeeklyForecast from '../WeeklyForecast/WeeklyForecast';
import { ThemeProvider } from '@emotion/react';
import { theme } from '../Reusable/TableStyle';

const Weather = () => {
  const [todayWeather, setTodayWeather] = useState(null);
  const [todayForecast, setTodayForecast] = useState([]);
  const [weekForecast, setWeekForecast] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(false);



  const searchChangeHandler = async (enteredData) => {
    const [latitude, longitude] = enteredData.value.split(' ');

    setIsLoading(true);

    const currentDate = transformDateFormat();
    const date = new Date();
    let dt_now = Math.floor(date.getTime() / 1000);

    try {
      const [todayWeatherResponse, weekForecastResponse] =
        await fetchWeatherData(latitude, longitude);
      const all_today_forecasts_list = getTodayForecastWeather(
        weekForecastResponse,
        currentDate,
        dt_now
      );

      const all_week_forecasts_list = getWeekForecastWeather(
        weekForecastResponse,
        ALL_DESCRIPTIONS
      );

      setTodayForecast([...all_today_forecasts_list]);
      setTodayWeather({ city: enteredData.label, ...todayWeatherResponse });
      setWeekForecast({
        city: enteredData.label,
        list: all_week_forecasts_list,
      });
    } catch (error) {
      setError(true);
    }

    setIsLoading(false);
  };



 

  if (error) {
    appContent = (
      <ErrorBox
        margin="3rem auto"
        flex="inherit"
        errorMessage="Something went wrong"
      />
    );
  }

  if (isLoading) {
    appContent = (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          width: '100%',
          minHeight: '500px',
        }}
      >
        <LoadingBox value="1">
          <Typography
            variant="h3"
            component="h3"
            sx={{
              fontSize: { xs: '10px', sm: '12px' },
              color: 'rgba(255, 255, 255, .8)',
              lineHeight: 1,
              fontFamily: 'Poppins',
            }}
          >
            Loading...
          </Typography>
        </LoadingBox>
      </Box>
    );
  }

  return (
  <>




<ThemeProvider theme={theme}>
<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Container
          sx={{
            maxWidth: { xs: '95%', sm: '80%', md: '1100px' },
            width: '100%',
            margin: 'auto',
            padding: '1rem',
            borderRadius: {
              xs: 'none',
              sm: '0 0 1rem 1rem',
            },
            boxShadow: {
              xs: 'none',
              sm: 'rgba(0,0,0, 0.5) 0px 10px 15px -3px, rgba(0,0,0, 0.5) 0px 4px 6px -2px',
            },
          }}
        >
      <Grid container columnSpacing={2}>
        <Grid item xs={12}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            sx={{
              width: '100%',
            }}
          >

            <UTCDatetime  sx={{
                height: { xs: '10px', sm: '15px', md: '20px' },
                width: 'auto',
              }} />
            <Search onSearchChange={searchChangeHandler}  sx={{
                height: { xs: '20px', sm: '25px', md: '30px' },
                width: 'auto',
              }}/>
            <Link
              href="/"
              target="_blank"
              underline="none"
              sx={{ display: 'flex' }}
            >
              <HomeIcon
                sx={{
                  fontSize: { xs: '20px', sm: '22px', md: '26px' },
                  color: 'white',
                  '&:hover': { color: '#2d95bd' },
                }}
              />
            </Link>
          </Box>
          
        </Grid>
    <Box
      xs={12}
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
      sx={{
        width: '100%',
        minHeight: '500px',
      }}
    >
      <SvgIcon
        component={SplashIcon}
        inheritViewBox
        sx={{ fontSize: { xs: '100px', sm: '120px', md: '140px' } }}
      />
      <Typography
        variant="h4"
        component="h4"
        sx={{
          fontSize: { xs: '12px', sm: '14px' },
          color: 'rgba(255,255,255, .85)',
          fontFamily: 'Poppins',
          textAlign: 'center',
          margin: '2rem 0',
          maxWidth: '80%',
          lineHeight: '22px',
        }}
      >
        Explore current weather data and 6-day forecast of more than 200,000
        cities!
      </Typography>
    </Box>
      <React.Fragment>
        <Grid item xs={12} md={todayWeather ? 6 : 12}>
          <Grid item xs={12}>
            <TodayWeather data={todayWeather} forecastList={todayForecast} />
          </Grid>
        </Grid>
        <Grid item xs={12} md={6}>
          <WeeklyForecast data={weekForecast} />
        </Grid>
      </React.Fragment>







      </Grid>
    </Container>
    </div>
    </ThemeProvider>
  </>);
}

export default Weather;
