import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import { styled, createTheme } from '@mui/material/styles';
import { alpha } from '@mui/material/styles';

import TextField from '@mui/material/TextField';
import InputBase from '@mui/material/InputBase';


// Create a Material-UI theme
const theme = createTheme({
  typography: {
    fontFamily: ['Poppins', 'Arial', 'sans-serif'].join(','),
  },
  overrides: {
    MuiCssBaseline: {
      '@global': {
        '@import': [
          "url('https://fonts.googleapis.com/css2?family=Roboto+Condensed:wght@300;400;700&display=swap')",
          "url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&family=Roboto+Condensed:wght@300;400;700&display=swap')",
        ],
        body: {
          margin: 0,
          fontFamily: ['Poppins', 'Arial', 'sans-serif'].join(','),
          WebkitFontSmoothing: 'antialiased',
          MozOsxFontSmoothing: 'grayscale',
          color: '#fff', // Set text color to white
          background: 'transparent', // Set background to transparent
          minHeight: '100vh',
        },
        code: {
          fontFamily: "source-code-pro, Menlo, Monaco, Consolas, 'Courier New', monospace",
        },
      },
    },
  },
});

const CustomTextField = styled(TextField)({
  backgroundColor: 'rgba(255, 255, 255, 0.6)', // Transparent white background
  color: 'black', // Black font color
  borderRadius: '25px', // Border radius
  borderColor: 'white', // Default border color
  borderWidth: '1px',
  '&:focus': { // Border color when TextField is focused
    borderColor: 'yellow', // Change to yellow when focused
  }
});

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
}));

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));





export {theme, StyledInputBase, StyledTableCell,CustomTextField,  };
