import React from 'react';
import ErrorBox from '../../Reusable/ErrorBox';
import AirConditionsItem from './AirConditionsItem';
import Layout from '../../Reusable/Layout';
import {  Typography } from '@mui/material';

const TodayWeatherAirConditions = ({ data }) => {
  const noDataProvided =
    !data || Object.keys(data).length === 0 || data.cod === '404';

  let content = <ErrorBox flex="1" type="error" />;

  function getWindDirection(degrees) {
    const directions = ['North', 'North-East', 'East', 'South-East', 'South', 'South-West', 'West', 'North-West'];
    const index = Math.round(degrees / 45) % 8;
    return directions[index];
  }
  const windDirection = getWindDirection(data.wind.deg);





  if (!noDataProvided)
    content = (
      <>
        <AirConditionsItem
          title="Real Feel"
          value={`${Math.round(data.main.feels_like-273)} °C`}
          type="temperature"
        />
        <AirConditionsItem
          title="Wind"
          value={`${Math.round(data.wind.speed  * 3.6).toFixed(2)} Km/h`}
          type="wind"
        />
        <AirConditionsItem
          title="Wind Direction"
          value={`${windDirection} `}
          type="wind"
        />
        <AirConditionsItem
          title="Clouds"
          value={`${Math.round(data.clouds.all).toFixed(2)} %`}
          type="clouds"
        />
        <AirConditionsItem
          title="Humidity"
          value={`${Math.round(data.main.humidity).toFixed(2)} %`}
          type="humidity"
        />
          <AirConditionsItem 
           title= "Sunrise"
           value={new Date(data.sys.sunrise * 1000).toLocaleTimeString([], {hour: '2-digit', minute: '2-digit', hour12: true})
          }
          />
          <AirConditionsItem
            title="Sunset"
            value={new Date(data.sys.sunset * 1000).toLocaleTimeString([], {hour: '2-digit', minute: '2-digit', hour12: true})
          }
          />
          <AirConditionsItem
            title="Forecast"
            value={new Date(data.dt * 1000).toLocaleString().toLocaleTimeString([], {hour: '2-digit', minute: '2-digit', hour12: true})
          }
          />
      </>
    );
  return (
    <Layout
      title="AIR CONDITIONS"
      content={content}
      mb="1rem"
      sx={{ marginTop: '2.9rem' }}
    />
  );
};

export default TodayWeatherAirConditions;
