import { createTheme } from '@mui/material/styles';


// Create a Material-UI theme
const theme = createTheme({
  typography: {
    fontFamily: ['Poppins', 'Arial', 'sans-serif'].join(','),
  },
  overrides: {
    MuiCssBaseline: {
      '@global': {
        '@import': [
          "url('https://fonts.googleapis.com/css2?family=Roboto+Condensed:wght@300;400;700&display=swap')",
          "url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&family=Roboto+Condensed:wght@300;400;700&display=swap')",
        ],
        body: {
          margin: 0,
          fontFamily: ['Poppins', 'Arial', 'sans-serif'].join(','),
          WebkitFontSmoothing: 'antialiased',
          MozOsxFontSmoothing: 'grayscale',
          color: '#fff', // Set text color to white
          background: 'transparent', // Set background to transparent
          minHeight: '100vh',
        },
        code: {
          fontFamily: "source-code-pro, Menlo, Monaco, Consolas, 'Courier New', monospace",
        },
      },
    },
  },
});







export {theme  };
