import React from 'react';
import { Typography } from '@mui/material';

const Time = ({ timezone }) => {
  const now = new Date(); // Get the current date and time
  const offsetMilliseconds = timezone * 1000; // Convert timezone offset from seconds to milliseconds
  const utcTime = new Date(now.getTime() + offsetMilliseconds).toLocaleString('en-US', { // Adjust time based on timezone offset
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: 'UTC', // Display time in UTC timezone
  });

  return (
    <Typography
      variant="h3"
      component="h3"
      sx={{
        fontSize: '20px',
        color: 'rgba(255, 255, 255, .7)',
        fontFamily: 'Poppins',
      }}
    >
      {utcTime}
    </Typography>
  );
};

export default UTCTime;
