import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { ThemeProvider } from '@mui/material/styles';
import TableSortLabel from '@mui/material/TableSortLabel';
import SearchIcon from '@mui/icons-material/Search';
import { InputBase, IconButton, List, ListItem } from '@mui/material';
import { StyledTableCell, theme } from './../Reusable/TableStyle';
import { fetchCitiesData } from './../../api/Api';

const Homepage = () => {
  const [citiesData, setCitiesData] = useState([]);
  const [order, setOrder] = useState('asc');
  const [orderBy, setOrderBy] = useState('id');
  const [searchValue, setSearchValue] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchCitiesData();
        setCitiesData(data);
      } catch (error) {
        console.error('Error fetching cities data:', error);
      }
    };

    fetchData();
  }, []);

  const columns = [
    { label: 'ID', dataKey: 'id', width: 10 },
    { label: 'City Name', dataKey: 'name', width: 200 },
    { label: 'Country', dataKey: 'country', width: 150 },
    { label: 'Timezone', dataKey: 'timezone', width: 150 },
    { label: 'Alternate Names', dataKey: 'alternate', width: 150 },
  ];

  const rows = citiesData.map((row, index) => ({
    id: index + 1,
    name: row.name,
    country: row.label_en,
    timezone: row.timezone,
    alternate: Array.isArray(row.alternate_names) ? row.alternate_names.slice(0, 3).join(', ') : '',
    coordinates: row.coordinates,
  }));

  const descendingComparator = (a, b, orderBy) => {
    if (b[orderBy] < a[orderBy]) {
      return -1;
    }
    if (b[orderBy] > a[orderBy]) {
      return 1;
    }
    return 0;
  };

  const getComparator = (order, orderBy) => {
    return order === 'desc'
      ? (a, b) => descendingComparator(a, b, orderBy)
      : (a, b) => -descendingComparator(a, b, orderBy);
  };

  const stableSort = (array, comparator) => {
    const stabilizedThis = array.map((el, index) => [el, index]);
    stabilizedThis.sort((a, b) => {
      const order = comparator(a[0], b[0]);
      if (order !== 0) {
        return order;
      }
      return a[1] - b[1];
    });
    return stabilizedThis.map((el) => el[0]);
  };

  const filterRows = () => {
    if (searchValue.trim() === '') {
      return rows;
    } else {
      return rows.filter((row) => {
        return Object.entries(row).slice(1).some(([key, value]) =>
          key !== 'id' && value.toString().toLowerCase().includes(searchValue.toLowerCase())
        );
      });
    }
  };

  const sortedRows = stableSort(filterRows(), getComparator(order, orderBy));

  const handleSearch = (event) => {
    setSearchValue(event.target.value);
  };

  const handleRequestSort = (property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  return (
    <>
      <ThemeProvider theme={theme}>
        <Paper
          component="form"
          sx={{ p: '2px 4px', display: 'flex', alignItems: 'center', width: 400 }}
        >
          <InputBase
            sx={{ ml: 1, flex: 1 }}
            placeholder="Searchâ€¦"
            inputProps={{ 'aria-label': 'search' }}
            value={searchValue}
            onChange={handleSearch}
          />
          <IconButton sx={{ p: '10px' }} aria-label="search">
            <SearchIcon />
          </IconButton>
        </Paper>

        <List>
          {sortedRows.map((user) => (
            <ListItem key={user.id}>{user.name}</ListItem>
          ))}
        </List>

        <TableContainer component={Paper} sx={{ maxHeight: 500 }}>
          <Table stickyHeader sx={{ minWidth: 700 }} size="small">
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                  <StyledTableCell
                    key={column.dataKey}
                    align="center"
                  >
                    <TableSortLabel
                      active={orderBy === column.dataKey}
                      direction={orderBy === column.dataKey ? order : 'asc'}
                      onClick={() => handleRequestSort(column.dataKey)}
                    >
                      {column.label}
                    </TableSortLabel>
                  </StyledTableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedRows.map((row) => (
                <TableRow key={row.id} hover>
                  {columns.map((column, index) => (
                    <StyledTableCell key={column.dataKey} align="center">
                      {index === 1 ? ( // Check if it's the name column
                        <Link to={`/weather?lat=${row.coordinates.lat}&lon=${row.coordinates.lon}`}>
                          {row[column.dataKey]}
                        </Link>
                      ) : (
                        row[column.dataKey]
                      )}
                    </StyledTableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </ThemeProvider>
    </>
  );
};

export default Homepage;
