import React from 'react';
import { Typography } from '@mui/material';

const UTCTime = ({ timezone }) => {
  const utcTime = new Date().toLocaleString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: timezone,
  });

  const sign = timezone >= 0 ? '+' : '-'; // Determine the sign of the timezone offset
  const absTimezone = Math.abs(timezone); // Get the absolute value of timezone

  return (
    <Typography
      variant="h3"
      component="h3"
      sx={{
        fontSize: '20px',
        color: 'rgba(255, 255, 255, .7)',
        fontFamily: 'Poppins',
      }}
    >
      {utcTime} GMT {sign}{absTimezone.toString().padStart(2, '0')}
    </Typography>
  );
};

export default UTCTime;
