import React from 'react';
import { Typography } from '@mui/material';

const UTCTime = ({ timezone }) => {
  // const now = new Date(); // Get the current date and time
  const utcTime = new Date(timezone + 60000).toLocaleString('en-US', { // Adjust time based on timezone offset
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: 'UTC', // Display time in UTC timezone
  });

  return (
    <Typography
      variant="h3"
      component="h3"
      sx={{
        fontSize: '20px',
        color: 'rgba(255, 255, 255, .7)',
        fontFamily: 'Poppins',
      }}
    >
      {utcTime} GMT
    </Typography>
  );
};

export default UTCTime;

function getTimeInTimezone(timezone) {
  // Get the current date and time
  let currentDate = new Date();

  // Create options object with timezone
  let options = {timeZone: timezone};

  // Format the date and time with the specified timezone
  let formattedDate = currentDate.toLocaleString('en-US', options);

  return formattedDate;
}

// Example usage:
let timezone = 'America/New_York'; // Replace this with the desired timezone
let currentTime = getTimeInTimezone(timezone);
console.log("Current time in", timezone, "is:", currentTime);
