import React from 'react';
import { Typography } from '@mui/material';

const UTCTime = ({ timezone }) => {
  const utcTime = new Date(timezone * 60000).toLocaleString('en-US', { // Adjust time based on timezone offset
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: 'UTC', // Display time in UTC timezone
  });

  return (
    <Typography
      variant="h3"
      component="h3"
      sx={{
        fontSize: '20px',
        color: 'rgba(255, 255, 255, .7)',
        fontFamily: 'Poppins',
      }}
    >
      {utcTime} GMT
    </Typography>
  );
};

export default UTCTime;
