import React from 'react';
import { Typography } from '@mui/material';

const UTCTime = () => {
  const utcTime = new Date().toLocaleString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: 'UTC',
  });

  return (
    <Typography
      variant="h3"
      component="h3"
      sx={{
        fontWeight: '400',
        fontSize: { xs: '15px', sm: '20px' },
        color: 'rgba(255, 255, 255, .7)',
        lineHeight: 1,
        paddingRight: '2px',
        fontFamily: 'Poppins',
      }}
    >
      {utcTime} GMT
    </Typography>
  );
};

export default UTCTime;
