import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Grid, Paper, InputBase, IconButton } from '@mui/material';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Container from '@mui/material/Container';
import { ThemeProvider } from '@mui/material/styles';
import TableSortLabel from '@mui/material/TableSortLabel';
import SearchIcon from '@mui/icons-material/Search';
import {  StyledTableCell, theme } from './../Reusable/TableStyle';
import { fetchCitiesData } from '../../api/Api';
import Autocomplete from '@mui/material/Autocomplete';


const Homepage = () => {
  const [citiesData, setCitiesData] = useState([]);
  const [order, setOrder] = useState('asc');
  const [orderBy, setOrderBy] = useState('id');
  const [searchValue, setSearchValue] = useState('');
  const [searchClicked, setSearchClicked] = useState(false); // State to track if search button is clicked

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchCitiesData();
        setCitiesData(data);
      } catch (error) {
        console.error('Error fetching cities data:', error);
      }
    };

    fetchData();
  }, []);

  const columns = [
    { label: 'ID', dataKey: 'id', width: 10 },
    { label: 'City Name', dataKey: 'name', width: 200 },
    { label: 'Country', dataKey: 'country', width: 150 },
    { label: 'Timezone', dataKey: 'timezone', width: 150 },
    { label: 'Alternate Names', dataKey: 'alternate', width: 150 },
  ];

  const rows = citiesData.map((row, index) => ({
    id: index + 1,
    name: row.name,
    country: row.label_en,
    timezone: row.timezone,
    alternate: Array.isArray(row.alternate_names) ? row.alternate_names.slice(0, 3).join(', ') : '',
    coordinates: row.coordinates,
  }));

  const descendingComparator = (a, b, orderBy) => {
    if (b[orderBy] < a[orderBy]) {
      return -1;
    }
    if (b[orderBy] > a[orderBy]) {
      return 1;
    }
    return 0;
  };

  const getComparator = (order, orderBy) => {
    return order === 'desc'
      ? (a, b) => descendingComparator(a, b, orderBy)
      : (a, b) => -descendingComparator(a, b, orderBy);
  };

  const stableSort = (array, comparator) => {
    const stabilizedThis = array.map((el, index) => [el, index]);
    stabilizedThis.sort((a, b) => {
      const order = comparator(a[0], b[0]);
      if (order !== 0) {
        return order;
      }
      return a[1] - b[1];
    });
    return stabilizedThis.map((el) => el[0]);
  };

  const filterRows = () => {
    if (searchValue.trim() === '' || !searchClicked) { // Apply filter only if searchClicked is true
      return rows;
    } else {
      return rows.filter((row) => {
        return Object.entries(row).slice(1).some(([key, value]) =>
          key !== 'id' && value.toString().toLowerCase().includes(searchValue.toLowerCase())
        );
      });
    }
  };
  

  const sortedRows = stableSort(filterRows(), getComparator(order, orderBy));

  const handleSearch = (event) => {
    setSearchValue(event.target.value);
  };

  const handleRequestSort = (property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const handleSearchClick = () => {
    setSearchClicked(true); // Set searchClicked to true when search button is clicked
  };
  
  const handleSearchSubmit = () => {
    // Apply filter logic here based on searchValue
    setSearchClicked(true); // Set searchClicked to true when search button is clicked
    // Clear input field after submission
    setSearchValue('');
  };
  
  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      // Prevent default form submission behavior
      event.preventDefault();
      // Apply filter when Enter key is pressed
      handleSearchSubmit();
    }
  };

  return (
    <>
       <ThemeProvider theme={theme}>
       <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
        <Container
          sx={{ maxWidth: { xs: '95%', sm: '80%', md: '1100px' }, width: '100%', margin: 'auto', padding: '1rem', borderRadius: {   xs: 'none',   sm: '1rem', }, boxShadow: {   xs: 'none',   sm: 'rgba(0,0,0, 0.5) 0px 10px 15px -3px, rgba(0,0,0, 0.5) 0px 4px 6px -2px', },
          }}
        >
<Grid container justifyContent="center" marginTop="1rem" marginBottom="1rem">
  <Grid item xs={12} sm={6} md={4} lg={3} >
  <Paper
      component="form"
      onSubmit={(e) => {
        e.preventDefault(); // Prevent default form submission behavior
        handleSearchSubmit();
      }}
      sx={{
        display: 'flex',
        alignItems: 'center',
        padding: 'dense',
        backgroundColor: 'rgba(255, 255, 255, 0.3)', // Transparent white background
        color: 'white', // White font color
        '& .MuiAutocomplete-root': {
          color: 'white', // White font color for Autocomplete
        },
        '& .MuiInputBase-root': {
          color: 'white', // White font color for InputBase
        },
        '& .MuiIconButton-root': {
          color: 'white', // White font color for IconButton
        },
      }}
    >
      <Autocomplete
        sx={{ ml: 1, flex: 1 }}
        value={searchValue}
        onChange={(event, newValue) => setSearchValue(newValue)}
        options={citiesData.map((option) => option.name)}
        renderInput={(params) => (
          <InputBase
            {...params}
            placeholder="Search ..."
            sx={{ ml: 1, flex: 1, color: 'white' }} // White font color for InputBase
            value={searchValue}
            onChange={handleSearch}
            inputProps={{ ...params.inputProps, 'aria-label': 'search users', onKeyDown: handleKeyDown }}
          />
        )}
      />

      <IconButton type="submit" sx={{ p: '10px' }} aria-label="search">
        {/* Add type="submit" to make the button trigger form submission */}
        <SearchIcon />
      </IconButton>
    </Paper>

  </Grid>
</Grid>
<TableContainer component={Paper} sx={{ maxHeight: 500, backgroundColor: 'transparent', overflow: 'auto', '&::-webkit-scrollbar': { width: '8px' }, '&::-webkit-scrollbar-thumb': { backgroundColor: 'rgba(255, 255, 255, 0.5)', borderRadius: '4px' } }}>
  <Table stickyHeader sx={{ minWidth: 700 }} size="small">
    <TableHead sx={{ backgroundColor: 'rgba(255, 255, 255, 0.5)' }}>
      <TableRow>
        {columns.map((column) => (
          <StyledTableCell
            key={column.dataKey}
            align="center"
            sx={{ backgroundColor: 'rgba(255, 255, 255, 0.5)', color: 'black' }}
          >
            <TableSortLabel
              active={orderBy === column.dataKey}
              direction={orderBy === column.dataKey ? order : 'asc'}
              onClick={() => handleRequestSort(column.dataKey)}
              sx={{ color: 'black' }} // White font color for TableSortLabel
            >
              {column.label}
            </TableSortLabel>
          </StyledTableCell>
        ))}
      </TableRow>
    </TableHead>

    <TableBody>
      {sortedRows.map((row) => (
        <TableRow key={row.id} hover>
          {columns.map((column, index) => (
            <StyledTableCell key={column.dataKey} align="center" sx={{ color: 'white', borderBottom: '1px solid black' }}>
              {index === 1 ? (
                
                <Link to={`/weather?lat=${row.coordinates.lat}&lon=${row.coordinates.lon}`} style={{ color: 'yellow', textDecoration: 'none' }}>
                  {row[column.dataKey]}
                </Link>
              ) : (
                row[column.dataKey]
              )}
            </StyledTableCell>
          ))}
        </TableRow>
      ))}
    </TableBody>
  </Table>
</TableContainer>




      </Container>
    </div>
    </ThemeProvider>
    </>
  );
};

export default Homepage;
