import { MONTHS, DAYS } from './DateConstants';

const date = new Date();
export const Today = date.toLocaleDateString('en-US', {
  weekday: 'long',
});
export const MonthYear = date.toLocaleDateString('en-US', {
  day:'2-digit',
  month: 'short',
  year:'numeric'
});

export function getWeekDays() {
  const dayInAWeek = new Date().getDay();
  const days = DAYS.slice(dayInAWeek, DAYS.length).concat(
    DAYS.slice(0, dayInAWeek)
  );
  return days;
}

export function getDayMonthFromDate() {
  const month = MONTHS[date.getMonth()].slice(0, 3);
  const day = date.getUTCDate();

  return day + ' ' + month;
}

export function transformDateFormat() {
  const month = date.toLocaleString('en-US', { month: '2-digit' });
  const day = date.toLocaleString('en-US', { day: '2-digit' });
  const year = date.getFullYear();
  const time = date.toLocaleString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hourCycle: 'h23',
  });

  const newFormatDate = year.toString().concat('-', month, '-', day, ' ', time);
  return newFormatDate;
}

export function getUTCDatetime() {
  const utcTime = date.toLocaleString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: 'UTC',
  });

  return utcTime;
}

export function getUTCTime() {
  const utcTime = date.toLocaleString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hourCycle: 'h23',
    timeZone: 'UTC',
  });

  return utcTime;
}


import { Box, Typography } from '@mui/material';
import React from 'react';

const CityDateDetail = (props) => {
  // Extract timezone from the weather response
  const { timezone } = props.weatherResponse;

  // Convert the timezone offset to hours and minutes
  const timezoneOffsetHours = Math.abs(Math.floor(timezone / 3600));
  const timezoneOffsetMinutes = Math.abs(Math.floor((timezone % 3600) / 60));

  // Format the timezone offset
  const formattedTimezoneOffset = `${timezone >= 0 ? '+' : '-'}${timezoneOffsetHours}:${timezoneOffsetMinutes}`;

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        height: '100%',
      }}
    >
      <Typography
        variant="h3"
        component="h3"
        sx={{
          fontFamily: 'Poppins',
          fontWeight: '600',
          fontSize: { xs: '12px', sm: '14px', md: '16px' },
          color: 'white',
          textTransform: 'uppercase',
          lineHeight: 1,
          marginBottom: '8px',
        }}
      >
        Timezone: UTC{formattedTimezoneOffset}
      </Typography>
      {/* You can add other content related to timezone here */}
    </Box>
  );
};

export default CityDateDetail;
