import React from 'react';
import { Typography } from '@mui/material';

const UTCTime = ({ timezone }) => {
  const formatTime = (utcTime, timezoneOffset) => {
    const localTime = new Date(utcTime * 1000 + timezoneOffset * 1000);
    const hours = localTime.getHours();
    const minutes = localTime.getMinutes();
    const ampm = hours >= 12 ? 'PM' : 'AM';
    const formattedHours = hours % 12 || 12;
    const formattedMinutes = minutes < 10 ? `0${minutes}` : minutes;
    return `${formattedHours}:${formattedMinutes} ${ampm}`;
  };

  const localTime = new Date(utcTime * 1000 + timezoneOffset * 1000);
  const utcTime = localTime.toLocaleString('en-US', { // Adjust time based on timezone offset
    hour: '2-digit',
    minute: '2-digit',
    hourCycle: 'h12',
    timeZone: 'UTC', // Display time in UTC timezone
  });

  return (
    <Typography
      variant="h3"
      component="h3"
      sx={{
        fontSize: '20px',
        color: 'rgba(255, 255, 255, .7)',
        fontFamily: 'Poppins',
      }}
    >
      {utcTime} GMT
    </Typography>
  );
};

export default UTCTime;
